import { Pipe, PipeTransform } from '@angular/core'

/**
 * A custom Angular pipe for formatting numbers with thousand suffixes (e.g., K, M, G).
 *
 * @example
 * // In your component template:
 * // Assuming 'value' is a number.
 * {{ value | thousandSuff:'round':2 }}
 *
 * @param {number} input - The number to format.
 * @param {string} round - Optional. Specify 'round' to round the number to the nearest whole number.
 * @param {number} toFixedArgs - Optional. The number of decimal places to round to.
 * @returns {string} - The formatted number with suffix.
 */

@Pipe({ name: 'thousandSuff' })
export class ThousandSuffixesPipe implements PipeTransform {
  /**
   * Transforms a number into a formatted string with thousand suffixes (e.g., K, M, G).
   *
   * @param {number} input - The number to format.
   * @param {string} round - Optional. Specify 'round' to round the number to the nearest whole number.
   * @param {number} toFixedArgs - Optional. The number of decimal places to round to.
   * @returns {string} - The formatted number with suffix.
   */
  transform(input: any, round?: any, toFixedArgs?: number): string {
    const suffixes = ['k', 'M', 'G', 'T', 'P', 'E']

    // Handle special cases
    if (Number.isNaN(input) || input === null || (!input && input !== 0)) {
      return 'âˆž'
    }

    // If input is less than 1000, return it as is (optionally rounded)
    if (input < 1000) {
      if (round) {
        return String(Math.round(input))
      }
      return String(input)
    }

    // Calculate the appropriate suffix and format the number
    const exp = Math.floor(Math.log(input) / Math.log(1000))
    return (input / Math.pow(1000, exp)).toFixed(toFixedArgs) + suffixes[exp - 1]
  }
}

import { Pipe, PipeTransform } from '@angular/core'
import {
  DomSanitizer,
  SafeHtml,
  SafeResourceUrl,
  SafeScript,
  SafeStyle,
  SafeUrl,
} from '@angular/platform-browser'

/**
 * A custom Angular pipe for sanitizing and displaying trusted content.
 * This pipe can be used to bypass security and render trusted HTML, styles, scripts, URLs, or resource URLs.
 *
 * @example
 * // In your component template:
 * // Assuming 'htmlContent' is a string containing trusted HTML.
 * <div [innerHTML]="htmlContent | universalSafe:'html'"></div>
 *
 * @param {string} value - The content to sanitize and display.
 * @param {string} type - The type of content to sanitize. Can be 'html', 'style', 'script', 'url', or 'resourceUrl'.
 * @returns {SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl} - The sanitized and trusted content.
 */
@Pipe({
  name: 'universalSafe',
})
export class UniversalSafePipe implements PipeTransform {
  /**
   * Creates an instance of UniversalSafePipe.
   *
   * @param {DomSanitizer} sanitizer - The Angular DOM sanitizer service.
   */
  constructor(protected sanitizer: DomSanitizer) { }

  /**
   * Sanitizes and returns trusted content based on the specified type.
   *
   * @param {string} value - The content to sanitize and display.
   * @param {string} type - The type of content to sanitize. Can be 'html', 'style', 'script', 'url', or 'resourceUrl'.
   * @returns {SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl} - The sanitized and trusted content.
   * @throws {Error} - If an invalid `type` is provided.
   */
  public transform(value?: string, type = 'html'): SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl {
    if (!value) {
      return ''
    }
    switch (type) {
      case 'html':
        return this.sanitizer.bypassSecurityTrustHtml(value)
      case 'style':
        return this.sanitizer.bypassSecurityTrustStyle(value)
      case 'script':
        return this.sanitizer.bypassSecurityTrustScript(value)
      case 'url':
        return this.sanitizer.bypassSecurityTrustUrl(value)
      case 'resourceUrl':
        return this.sanitizer.bypassSecurityTrustResourceUrl(value)
      default:
        throw new Error(`Invalid Safe sanitizer: ${type}`)
    }
  }
}

